import React, { useMemo, useState } from "react";
import './styles/App.css'
import PostList from "./components/PostList";
import PostForm from "./components/PostForm";
import MySelect from "./components/UI/select/MySelect";
import MyInput from "./components/UI/input/MyInput";
import PostFilter from "./components/PostFilter";

function App() {
  const [posts, setPosts] = useState([
    {id: 1, title: 'JavaScript', body: 'JavaScript - это язык программирования'},
    {id: 2, title: 'Python', body: 'Python - это язык программирования'},
    {id: 3, title: 'C++', body: 'C++ - это язык программирования'},
  ])
  const [filter, setFilter] = useState({sort: '', query: ''})

  const sortedPosts = useMemo( () => {
    console.log("ФУНКЦИА ОТРАБОТАТ!")
    if(filter.sort) {
      return [...posts].sort((a, b) => a[filter.sort].localeCompare(b[selectedSort]))
    }
    return posts;
  }, [filter.sort, posts])

  const sortedAndSearchedPosts = useMemo( () => {
    return sortedPosts.filter(post => post.title.toLowerCase().includes(searchQuery))
  }, [searchQuery, sortedPosts])

  const createPost = (newPost) => {
    setPosts( [...posts, newPost])
  }

  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id))
  }

  const sortPosts = (sort) => {
    setSelectedSort(sort);
    // console.log(sort)
  }

  return (
    <div className="App">
      <PostForm create={createPost}/>
      <hr style={{margin: '15px 0'}}/>
      <PostFilter />
      {sortedAndSearchedPosts.length
        ? <PostList remove={removePost} posts={sortedAndSearchedPosts} title="Список Постов"/>
        : <h1 style={{textAlign: 'center'}}>Постов нет!</h1>

      }
    </div>
  );
}

export default App;
