import React, { useMemo, useState } from "react";
import './styles/App.css'
import PostList from "./components/PostList";
import PostForm from "./components/PostForm";
import MySelect from "./components/UI/select/MySelect";
import MyInput from "./components/UI/input/MyInput";

function App() {
  const [posts, setPosts] = useState([
    {id: 1, title: 'JavaScript', body: 'JavaScript - это язык программирования'},
    {id: 2, title: 'Python', body: 'Python - это язык программирования'},
    {id: 3, title: 'C++', body: 'C++ - это язык программирования'},
  ])
  const [selectedSort, setSelectedSort] = useState('')
  const [searchQuery, setSearchQuery] = useState('')

  function getSortedPosts() {
    console.log("ФУНКЦИА ОТРАБОТАТ!")
    if(selectedSort) {
      return [...posts].sort((a, b) => a[selectedSort].localeCompare(b[selectedSort]))
    }
    return posts;
  }


  const sortedPosts = useMemo()

  const createPost = (newPost) => {
    setPosts( [...posts, newPost])
  }

  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id))
  }

  const sortPosts = (sort) => {
    setSelectedSort(sort);
    // console.log(sort)
  }

  return (
    <div className="App">
      <PostForm create={createPost}/>
      <hr style={{margin: '15px 0'}}/>
      <div>
        <MyInput
          value={searchQuery}
          onChenge={e => setSearchQuery(e.target.value)}
          placeholder="Поиск"
        />
        <MySelect 
          value={selectedSort}
          onChenge={sortPosts}
          defaultValue="Сортировка по"
          options={[
            {value: 'title', name: 'По названию'},
            {value: 'body', name: 'По описанию'},
          ]}
        />
      </div>

      {posts.length
        ? <PostList remove={removePost} posts={sortedPosts} title="Список Постов"/>
        : <h1 style={{textAlign: 'center'}}>Постов нет!</h1>

      }
    </div>
  );
}

export default App;
